    componentDidMount(){
        this.setState({characters: this.shuffleData(this.state.characters)})
    }
    //[{id: 1, name: "Farley" , image" "/img/meow.jpg, clicked: false"}]
    shuffleData = data => {
        let i = data.length - 1;
        while (i > 0) {
          const j = Math.floor(Math.random() * (i + 1));
          const temp = data[i];
          data[i] = data[j];
          data[j] = temp;
          i--;
