    handleItemClick = id => {
        let guessedCorrectly = false;
        const newClick = this.state.data.map(item => {
          const newItem = { ...item };
          if (newItem.id === id) {
            if (!newItem.clicked) {
              newItem.clicked = true;
              guessedCorrectly = true;
            }
          }
          return newItem;
        });
        guessedCorrectly
          ? this.handleCorrectGuess(newClick)
          : this.handleIncorrectGuess(newClick);
      };




    handleCorrectClick = (newClick) => {
        // update state
        //on that obj you will need to toggle clicked to true
        // compare current score to high score
        // call shuffle
        const {highScore, score} = this.state;
        const newScore = score +1; 
        const newHighScore = Math.max(newScore, highScore);

        this.setState ({
            characters: this.shuffleData(newClick),
            score: newScore,
            highScore: newHighScore
        });
    };

    handleIncorrectClick = characters => {
        //  reset state to original values
        // call shuffle
        this.setState ({
            characters: this.resetData(characters),
            score: 0
        });
    };

    resetData = characters => {
        const resetCharacters = characters.map(item => ({ ...item, clicked: false }));
        return this.shuffleData(resetCharacters);
      };

    componentDidMount(){
        this.setState({characters: this.shuffleData(this.state.characters)})
    }


    render() {
        return(
        <div>
            <h1>Ta-Da</h1>
            <h1>Score:{this.state.score}</h1>
            <h1>High Score:{this.state.highScore}</h1>

            {this.state.characters.map(item => (
                <Clicked
                key={item.id}
                id={item.id}
                shake={!this.state.score && this.state.topScore}
                handleClick={this.handleItemClick}
                image={item.image}
                />
                ))}
            </div>
    )}
};

export default Game;
